function t456_setListMagin(recid,imglogo){var rec=document.getElementById('rec'+recid);if(!rec||window.innerWidth<=980)return;var menu=rec.querySelector('.t456');var leftSide=menu?menu.querySelector('.t456__leftwrapper'):null;var list=menu?menu.querySelector('.t456__list'):null;var leftSideWidth=leftSide?leftSide.offsetWidth:0;if(list)list.style.marginRight=(imglogo?leftSideWidth:leftSideWidth+30)+'px'}

function setCookie(name, value, days) {
    let expires = "";
    if (days) {
        const date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + encodeURIComponent(value) + expires + "; path=/";
}

function getCookie(name) {
    const nameEQ = name + "=";
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
        cookie = cookie.trim();
        if (cookie.startsWith(nameEQ)) {
            return decodeURIComponent(cookie.substring(nameEQ.length));
        }
    }
    return null; // Return null if cookie not found
}
function changeMainPage() {
    var currentPageNumber = parseInt(getCookie("mainpage"));
    var nextPageNumber = 0;
    if(currentPageNumber != null && currentPageNumber !== null && Number.isInteger(currentPageNumber)) {
        nextPageNumber = currentPageNumber + 1;
        if(nextPageNumber > 3) {
            nextPageNumber = 0;
        }
    }

    setCookie("mainpage", nextPageNumber, 365);

    var webpage = "index.html";
    if(nextPageNumber > 0) {
        webpage = "index_00"+nextPageNumber+".html";
    }
    window.location.href = webpage;
}

var currentPageNumber = parseInt(getCookie("mainpage"));
if(currentPageNumber > 0) {
    document.querySelectorAll('.t456__logowrapper a').forEach(item => {
        item.href = "index_00"+currentPageNumber+".html";
    });
} else {
    document.querySelectorAll('.t456__logowrapper a').forEach(item => {
        item.href = "index.html";
    });
}

